import React, { useState } from 'react';
import WebcamCapture from './WebcamCapture';
import ThreeDObject from './ThreeDObject';

function App() {
  // WebcamCaptureコンポーネントからの値を保持する状態
  const [webcamValue, setWebcamValue] = useState<number | undefined>(undefined);

  // WebcamCaptureコンポーネントを呼び出して値を取得
  const valueFromWebcam = WebcamCapture();

  // WebcamCaptureからの値を設定
  if (typeof valueFromWebcam === 'number') {
    setWebcamValue(valueFromWebcam);
  }

  return (
    <div>
      <WebcamCapture /> {/* WebcamCaptureコンポーネントを表示 */}
      {webcamValue !== undefined && (
        <ThreeDObject color={webcamValue === 1 ? 0x00ff00 : 0xff00ff} />
      )}
    </div>
  );
}

export default App;
